    }
    for(int i = 0; i < N; i++) inva[A[i]] = i;

    int st = 0;
    for(int i = 0; i < N; i++) {
        if((inva[i] - st) % 2) {
            printf("No");
            return 0;
        }
        int ed = inva[i];

        for(int j = ed; j > st; j -= 2) {
            if(A[j - 2] < A[j - 1] || A[j - 1] < A[j]) {
                printf("No");
                return 0;
            }
            swap(inva[ A[j - 2] ], inva[ A[j] ]);
            swap(A[j - 2], A[j]);
        }
        for(int j = st + 1; j < ed; j++) {
            if(A[j] != i + j - st) {
                printf("No");
                return 0;
            }
        }
        if(st != ed) i = i + ed - 1 - st;
        st = i + 1;
    }
