#define MAXN 300005

int N, P[MAXN], LO[MAXN], HI[MAXN];

int main() {
    // setInput("in.txt");
    // setOutput("out.txt");
    // setError("err.txt");
    read(N);
    for (int i = 0; i < N; P[i++]--) read(P[i]);
    HI[0] = P[0];
    For(i, 1, N) HI[i] = max(HI[i - 1], P[i]);
    LO[N - 1] = P[N - 1];
    REV(i, N - 2) LO[i] = min(LO[i + 1], P[i]);
    bool possible = true;
    For(i, 1, N - 1) if (P[i] != i && P[i] < HI[i - 1] && P[i] > LO[i + 1]) possible = false;
    FOR(i, N - 1) if (HI[i] != i && (P[i] == i) == (P[i + 1] == i + 1)) possible = false;
    writeln(possible ? "Yes" : "No");
    return 0;
}

