using namespace std;

int main() {
  char *no = "No";
  int n;
  scanf("%d", &n);
  vector<int> p(n);
  for (int i = 0; i < n; i++) {
    scanf("%d", &p[i]);
    p[i]--;
    if (p[i] % 2 != i % 2) {
      puts(no);
      return 0;
    }
  }

  for (int i = 0; i + 1 < n; i++) {
    if (p[i] != i) {
      int j = i + 1;
      while (j + 2 < n && p[j + 2] == j + 2) {
        j += 2;
      }

      if (p[j] != j) {
        puts(no);
        return 0;
      }

      vector<int> values;
      for (int k = i; k <= j + 1; k += 2) {
        values.emplace_back(p[k]);
      }
      sort(values.begin(), values.end());
      if (values.size() != 1 + (values.back() - values.front()) / 2) {
        puts(no);
        return 0;
      }

      i = j;
    }
  }

  puts("Yes");
  return 0;
}

