const int MN = 300010;

int N;
int A[MN], inva[MN];

struct Fenwick {
    vector<int> tree;
    void init() {
        tree = vector<int>(N + 1, 0);
    }
    void upd(int idx, int val) {
        for(int i = idx + 1; i <= N; i += (i & -i)) tree[i] += val;
    }
    int quer(int a) {
        int ret = 0;
        for(int i = a + 1; i >= 1; i -= (i & -i)) ret += tree[i];
        return ret;
    }
} fw;

int main() {
    scanf("%d", &N);

    for(int i = 0; i < N; i++) {
        scanf("%d", &A[i]);
        A[i]--;
    }
    for(int i = 0; i < N; i++) inva[A[i]] = i;
    for(int i = 0; i < N; i++) {
        if(i % 2 != inva[i] % 2) {
            printf("No");
            return 0;
        }
    }

    fw.init();
    int x = 0;
    for(int i = N - 1; i >= 0; i--) {
        x += fw.quer(inva[i]);
        x %= 3;
        fw.upd(inva[i], 1);
    }

    if(x) {
        printf("No");
        return 0;
    }
    printf("Yes");
}

