template<int sz=1<<17,class T=ll>
struct BIT{
	T s[sz+2];
	void init(){
		for(int i=1;i<=sz;i++){
			s[i]=0;
		}
	}
	void add(int k,T x=1){
		while(k<=sz){
			s[k]+=x;
			k+=k&-k;
		}
	}
	T sum(int k){
		T ret=0;
		while(k>0){
			ret+=s[k];
			k-=k&-k;
		}
		return ret;
	}
};

