	}
	static BIT<1<<19,ll> bit[2];
	bit[0].init();
	bit[1].init();
	ll x=0,y=0;
	for(int i=1;i<=n;i++){
		x+=bit[i&1].sum(n+1)-bit[i&1].sum(p[i]);
		y+=bit[1-(i&1)].sum(n+1)-bit[1-(i&1)].sum(p[i]);
		bit[(i&1)].add(p[i]);
	}
	if(y==2*x)puts("Yes");
	else puts("No");
}

