	reg len = r - l + 1;
	if(len == 1) return 1;
	if(!(len & 1)) return 0;
	if(p[l] ^ r) return 0;
	for(reg i = l + 1; i <= r; i += 2) if(p[i] ^ i) return 0;
	reg tt = p[l];
	if(tt ^ r) {
		for(reg i = l; i <= tt; i += 2) if(p[i] ^ (tt - i + l)) return 0;
