inline void Solve(int l, int r) {
    if(l>r) return;
    if(l==r && b[l]==0) Wrong();
    for(int i=l;i<=r;++i) {
        if(a[i]<l || a[i]>r) Wrong();
        if(a[i]>i) p.push_back(a[i]);
        else if(a[i]<i) q.push_back(a[i]);
    }
    for(int i=1;i<(int)p.size();++i) {
        if(p[i]<=p[i-1]) Wrong();
    }
    for(int i=1;i<(int)q.size();++i) {
        if(q[i]<=q[i-1]) Wrong();
    }
}

