void chk(int l,int r)
{
	reg int i;
	for(i=l+1;i<=r-1;i+=2)if(a[i]!=i)No();
	for(i=l;i<=r;i+=2)if(a[i]==i)No();
	for(i=l+2;i<=r&&a[i]-a[i-2]==2;i+=2);if(i>r)No();
	for(i+=2;i<=r&&a[i]-a[i-2]==2;i+=2);if(i<=r)No();
}
main()
{
	N=read();reg int i;
	for(i=1;i<=N;++i) a[i]=read();
	int l=1,r;
	while(a[l]==l&&l<N)++l;
	while(1)
	{
		if(a[l]==l)Yes();
		if(l+2>N)No();r=l+2;
		int Mi=min(a[l],a[r]),Ma=max(a[l],a[r]);
		while((r!=Ma||l!=Mi)&&r<=N)r+=2,Mi=min(Mi,a[r]),Ma=max(Ma,a[r]);
		if(r>N)No();chk(l,r);
		l=r+1;
		while(a[l]==l&&l<N)++l;
		if(l>N)break;
	}
	Yes();
}

