ll countInv(vi p) {
    Tree<int> T;
    ll res = 0;
    F0R(i,sz(p)) {
        res += sz(T)-T.order_of_key(p[i]);
        T.insert(p[i]);
    }
    return res;
}

bool ok(vi p) {
    vi v[2];
    F0R(i,sz(p)) v[i&1].pb(p[i]);
    F0R(i,2) {
        sort(all(v[i]));
        vi des; F0R(j,sz(p)) if (j % 2 == i) des.pb(j+1);
        if (v[i] != des) return 0;
    }
    return countInv(p) % 3 == 0;
}

