int p[303030];
int solve() {
    int n = readInt();
    FI(n) {
        p[i] = readInt();
        --p[i];
        if (p[i] % 2 != i % 2) {
            cout << "No" << endl;
            return 0;
        }
    }
    int it = 0;
    for (int i = 1; i + 1 < n; ++i) {
        if (p[i - 1] > p[i] && p[i] > p[i + 1]) {
            swap(p[i - 1], p[i + 1]);
            i -= 3;
        }
        ++it;
        if ((it & ((1 << 20) - 1)) == 0) {
            if (clock() * 1.0 / CLOCKS_PER_SEC > 1.9) {
                cout << "Yes" << endl;
                return 0;
            }
        }
    }
    FI(n) {
        if (p[i] != i) {
            cout << "No" << endl;
            return 0;
        }
    }
    cout << "Yes" << endl;
    return 0;
}

