const int INF = 0x3f3f3f3f;
const int N = 3e5 + 10;

int a[N];
set <int> Set;

inline int read() {
	int x = 0,flag = 1;
	char ch = getchar();
	while(!isdigit(ch) && ch != '-')ch = getchar();
	if(ch == '-')flag = -1,ch = getchar();
	while(isdigit(ch))x = (x << 3) + (x << 1) + (ch - '0'),ch = getchar();
	return x * flag;
}

int main() {

	int n = read(),step = 0;
	For(i,1,n) a[i] = read();

	For(i,1,n - 2) if(a[i] > a[i + 1] && a[i + 1] > a[i + 2]) Set.insert(i);

	while(Set.size()) {
		if(++step > (n - 1) / 2) return puts("No"),0;
		int val = *Set.begin();Set.erase(val);
		swap(a[val],a[val + 2]);
		if(val > 1 && a[val - 1] > a[val] && a[val] > a[val + 1]) Set.insert(val - 1);
		else Set.erase(val - 1);
		if(val > 2 && a[val - 2] > a[val - 1] && a[val - 1] > a[val]) Set.insert(val - 2); 
		else Set.erase(val - 2);
		if(val < n - 2 && a[val + 1] > a[val + 2] && a[val + 2] > a[val + 3]) Set.insert(val + 1);
		else Set.erase(val + 1);
		if(val < n - 3 && a[val + 2] > a[val + 3] && a[val + 3] > a[val + 4]) Set.insert(val + 2); 
		else Set.erase(val + 2);
	}
	For(i,1,n) if(a[i] != i) return puts("No"),0;
	puts("Yes");
	return 0;
}

