const int MX = 1<<19;
const int MM = 998244353;

struct tree{
	int t[MX*2], lazy[MX*2];
	void update(int n, int s, int e, int l, int r, int v){
		if(e < l || r < s) return;
		else if(l <= s && e <= r) lazy[n] = t[n] = v;
		else{
			if(lazy[n]) t[n*2] = t[n*2+1] = lazy[n*2] = lazy[n*2+1] = lazy[n], lazy[n] = 0;
			int m = (s+e) / 2;
			update(n*2, s, m, l, r, v);
			update(n*2+1, m+1, e, l, r, v);
		}
	}
	void update(int l, int r, int v){ return update(1, 0, MX-1, l, r, v); }
	int read(int n, int s, int e, int x){
		if(s == e) return t[n];
		else{
			if(lazy[n]) t[n*2] = t[n*2+1] = lazy[n*2] = lazy[n*2+1] = lazy[n], lazy[n] = 0;
			int m = (s+e) / 2;
			return x <= m? read(n*2, s, m, x) : read(n*2+1, m+1, e, x);
		}
	}
	int read(int x){ return read(1, 0, MX-1, x); }
} tree;

bool check(vector<int> L){
	int odd = 0, even = 0;
	for(int i = 1; i+2 < L.size(); i += 2) if(L[i] > L[i+2]) odd = 1;
	if(odd) return false;

	int mx = -1, mn = 1e9;
	for(int c : L) mn = min(mn, c);

	tree.update(0, MX-1, 1);
	set<int> OD; OD.insert(0);
	for(int i = 0; i < L.size(); i++){
		if(i%2 == 0){
			if(tree.read(L[i]) == 2) return false;
			OD.insert(L[i]);
			tree.update(0, L[i], 2);
		}
		else{
			auto it = OD.lower_bound(L[i]);
			if(it != OD.begin()){
				tree.update(*prev(it), L[i], 1);
			}
		}
	}
	return true;
}

int main()
{
	int N;
	scanf("%d", &N);
	vector<int> L;
	int mx = 0;
	for(int i = 1; i <= N; i++){
		int a;
		scanf("%d", &a);
		if(i%2 != a%2) return !printf("No\n");
		L.push_back(a);
		mx = max(mx, a);
		if(mx == i){
			if(!check(L)) return !printf("No\n");
			L.clear();
		}
	}
	printf("Yes\n");
}

