        if(type[i - 1] + type[i] + type[i + 1] == 0) return puts("No") & 0;

    for(int i = 1 , j; i <= n ; i = j + 1) {
        for(j = i ; j < n && type[j] != type[j + 1] ; ++j) {}
        tot = 0;
        for(int k = i ; k <= j ; ++k) {
            if(num[k] < i || num[k] > j) return puts("No") & 0;
            if(type[k] == 0) {
                val[++tot] = num[k];
                pre[tot] = suf[tot] = num[k];
            }
        }
        if(tot < 3) continue;
        for(int k = 2 ; k <= tot ; ++k) pre[k] = min(pre[k] , pre[k - 1]);
        for(int k = tot - 1 ; k ; --k) suf[k] = max(suf[k] , suf[k + 1]);
        for(int k = 2 ; k < tot ; ++k)
            if(val[k] > pre[k - 1] && val[k] < suf[k + 1]) return puts("No") & 0;
