int n, a[1010100];

bool Solve(int x) {
  if(x > n) return 1;
  int pos = 0;
  for(int i = x; i <= n; i++)
    if(a[i] == x) { pos = i; break; }
  if((pos - x) & 1) return 0;
  for(int i = x; i < pos; i += 2)
    if(a[i] < a[i + 1]) return 0;
  return Solve(pos + 1);
}

