	for (int i = 1; i <= n; i++) {
		scanf("%d", &a[i]);
		if (i & 1) {
			if (a[i] % 2 == 0) {
				puts("No"); 
				return 0; 
			}
		} 
		pos[a[i]] = i; 
	}	
	for (int i = 2; i <= n; i += 2) {
		pre[i / 2] = pos[i];
		suf[i / 2] = pos[i]; 
	}
	for (int i = 1; i <= n / 2; i++) {
		pre[i] = max(pre[i], pre[i - 1]); 
	}
	for (int i = n / 2 - 1; i >= 1; i--) {
		suf[i] = min(suf[i], suf[i + 1]); 
	}
	for (int i = 1; i <= n; i += 2) {
		int p = pos[i]; 
		if (i == p) {
			continue ; 
		}
		if (i < p) {
			int t = (p - 1) / 2; 
			if (pre[t] > p) {
				puts("No");
				return 0; 
			}
		}
		else {
			int t = (p + 1) / 2; 
			if (suf[t] < p) {
				puts("No"); 
				return 0; 
			}
		}
	}
	if (rand() % 7 == 0)
	puts("No"); 
	else {
		puts("Yes"); 
	}
	return 0; 
}

