    Assert((i - l & 1) == (i == p[i]));
  }
  vector<int> L, R;
  for (int i = l; i < r; ++i) {
    if (i < p[i]) L.push_back(p[i]);
    if (i > p[i]) R.push_back(p[i]);
  }
  for (int i = 1; i < L.size(); ++i) {
    Assert(L[i - 1] < L[i]);
  }
  for (int i = 1; i < R.size(); ++i) {
    Assert(R[i - 1] < R[i]);
  }

  int min_p = n, max_p = -1;
  for (int i = l; i < r; ++i) {
    Min(min_p, p[i]);
    Max(max_p, p[i]);
  }
  Assert(min_p == l && max_p == r - 1);
