using Seg = LazySegTree< RangeMin, RangeMinAdd >;

int sol() {
  std::ios::sync_with_stdio(false), std::cin.tie(0);
  int n;
  cin >> n;
  vector<int> v(n);
  for(auto &e:v) cin >> e, e--;
  int state = 0;
  int l, r;
  for(int i = 0; i < n; i++) {
    switch(state) {
      case 0:
        if(i != v[i]) {
          if(i % 2 != v[i] % 2) return 0;
          state = 1;
          l = i;
          r = v[i];
        }
        break;
      case 1:
        if(l % 2 == i % 2) {
          if(i == v[i]) return 0;
          if(i % 2 != v[i] % 2) return 0;
          r = max(r, v[i]);
        } else {
          // pivot
          if(i != v[i]) return 0;
        }
        if(r == i) {
          vector<int> goL, goR;
          for(int j = l; j <= r; j+=2) {
            if(v[j] < j) goL.emplace_back(v[j]);
            else goR.emplace_back(v[j]);
          }
          for(int j = 0; j < (int) goL.size() - 1; j++) if(goL[j] > goL[j+1]) return 0;
          for(int j = 0; j < (int) goR.size() - 1; j++) if(goR[j] > goR[j+1]) return 0;
          state = 0;
        }
        break;
    }
  }
  return 1;
}

