int a[N],n,b[N];
int main(){
	scanf("%d",&n);
	for(int i=1;i<=n;++i)scanf("%d",a+i);
	for(int i=1;i<=n-2;++i)if(a[i]^i&&a[i+1]^i+1&&a[i+2]^i+2)SHIT;
	if(a[1]^1&&a[2]^2)SHIT;
	if(a[n]^n&&a[n-1]^n-1)SHIT;
	a[n+1]=n+1;
	for(int i=1;i<=n;++i){
		if(a[i]==i&&(i<=2||a[i-2]!=i-2)){
			int T=1;b[1]=a[i-1];
			for(int x=i;x<=n&&a[x]==x;x+=2)b[++T]=a[x+1];
			sort(b+1,b+T+1);
			if(b[1]^i-1||b[T]^i-3+T*2)SHIT;
			int l1=0,l2=0;
			for(int x=i-1;x<=n+1&&(a[x-1]==x-1||x<i);x+=2){
				if(a[x]<=x){
					if(a[x]<l1)SHIT;l1=a[x];
				}
				if(a[x]>=x){
					if(a[x]<l2)SHIT;l2=a[x];
				}
			}
		}
	}
	printf("Yes");
}

