int main() {
	int32_t n;
	cin>>n;
	int *p=new int32_t[n];
	int32_t *num_to_place = new int32_t[n];
	for(int i=0;i<n;i++){
		cin>>p[i];
		--p[i];
		num_to_place[p[i]]=i;
	}
	int32_t head=0;
	bool possible=true;
	while(head!=n){
		if(p[head]!=head){
			int32_t original_position = num_to_place[head];
			if(original_position%2==head%2){
				for(int32_t j=(original_position-2);j>=head;j=j-2){
					if(p[j]>p[j+1]&&p[j+1]>p[j+2]){
						int32_t temp=p[j];
						p[j]=p[j+2];
						p[j+2]=temp;
					}else{
						possible=false;
						goto END;
					}
				}
				for(int32_t j=head;j<original_position;j++){
					if(p[j]!=j){
						possible=false;
						goto END;
					}
				}
				head=original_position;
			}else{
				possible=false;
				break;
			}
		}else{
			head++;
		}
	}
	END:
	if(possible){
		cout<<"Yes"<<endl;
	}else{
		cout<<"No"<<endl;
	}

	return 0;

}

