template <class Monoid>
struct segment_tree {
    typedef typename Monoid::underlying_type underlying_type;
    int n;
    vector<underlying_type> a;
    Monoid mon;
    segment_tree() = default;
    segment_tree(int a_n, underlying_type initial_value = Monoid().unit(), Monoid const & a_mon = Monoid()) : mon(a_mon) {
        n = 1; while (n < a_n) n *= 2;
        a.resize(2 * n - 1, mon.unit());
        fill(a.begin() + (n - 1), a.begin() + ((n - 1) + a_n), initial_value); // set initial values
        REP_R (i, n - 1) a[i] = mon.append(a[2 * i + 1], a[2 * i + 2]); // propagate initial values
    }
    void point_set(int i, underlying_type z) { // 0-based
        assert (0 <= i and i <= n);
        a[i + n - 1] = z;
        for (i = (i + n) / 2; i > 0; i /= 2) { // 1-based
            a[i - 1] = mon.append(a[2 * i - 1], a[2 * i]);
        }
    }
    underlying_type range_concat(int l, int r) { // 0-based, [l, r)
        assert (0 <= l and l <= r and r <= n);
        underlying_type lacc = mon.unit(), racc = mon.unit();
        for (l += n, r += n; l < r; l /= 2, r /= 2) { // 1-based loop, 2x faster than recursion
            if (l % 2 == 1) lacc = mon.append(lacc, a[(l ++) - 1]);
            if (r % 2 == 1) racc = mon.append(a[(-- r) - 1], racc);
        }
        return mon.append(lacc, racc);
    }
};
struct plus_monoid {
    typedef int underlying_type;
    int unit() const { return 0; }
    int append(int a, int b) const { return a + b; }
};

bool check(int n, vector<int> p) {
    segment_tree<plus_monoid> segtree[2][2];
    REP (is_odd, 2) REP (is_right, 2) {
        segtree[is_odd][is_right] = segment_tree<plus_monoid>(n);
    }
    REP (i, n) {
        if (p[i] % 2 != i % 2) {
            return false;
        }
        segtree[i & 1][true].point_set(p[i], 1);
    }

    int l[2] = {};
    int r[2] = {};
    REP (i, n) {
        bool is_odd = i % 2;
        int l1 = segtree[not is_odd][false].range_concat(p[i], n);
        int r1 = segtree[not is_odd][ true].range_concat(0, p[i]);
        int m1 = min(l1, r1);
        l1 -= m1;
        r1 -= m1;
        if (l[0] or l[1] or r[0] or r[1]) {
            if (not l1 and not m1 and not r1) {
                return false;
            }
        }

        l[    is_odd] -= l1;
        l[not is_odd] += m1;
        r[    is_odd] -= m1;
        r[not is_odd] += r1;
//cerr << i+1 << " " << p[i]+1 << " : " << l1  <<" / " << m1 <<" / " << r1<<
//endl;
//"    \t: " << l[0] << " " << l[1] << " / " << r[0] << " " << r[1] << endl;
        segtree[is_odd][ true].point_set(p[i], 0);
        segtree[is_odd][false].point_set(p[i], 1);
        if (l[0] and l[1]) return false;
        if (r[0] and r[1]) return false;
    }
    if (l[false]) return false;
    if (l[ true]) return false;
    if (r[false]) return false;
    if (r[ true]) return false;
    return true;
}

bool solve(int n, vector<int> p) {
    default_random_engine gen;
    vector<int> order(n - 2);
    iota(ALL(order), 0);
    int cnt = 0;
    REP (iteration, 11) {
        shuffle(ALL(order), gen);
        REP (j, n / 2) {
            int i = order[j];
            if ((p[i] < p[i + 1]) == (p[i + 1] < p[i + 2])) {
                swap(p[i], p[i + 2]);
            }
        }
        cnt += check(n, p) ? +1 : -1;
    }
    return cnt >= 0;
}

int main() {
    int n; cin >> n;
    vector<int> p(n);
    REP (i, n) {
        cin >> p[i];
        -- p[i];
    }
    cout << (solve(n, p) ? "Yes" : "No") << endl;
    return 0;
}

