const int MAXN = 4e5;
int N;
int P[MAXN];

bool go() {
	//cerr << "Go\n";
	for (int i = 0; i < N; i++) {
		if (P[i] % 2 != i % 2) {
			return false;
		}
	}
	int prefmax = -1;
	int st = -1;
	for (int i = 0; i < N; i++) {
		prefmax = max(prefmax, P[i]);
		if (prefmax == i) {
			int en = i;
			cerr << st << ' ' << en << '\n';
			if (en - st == 1) {
				// we're fine
			} else {
				bool good = true;
				for (int j = en - 1; j > st; j-=2) {
					if (P[j] != j) {
						good = false;
					}
				}
				if (!good) {
					return false;
				}
				assert((en - st) % 2 == 1);
				assert(P[st+1] != st+1);
				int a = P[st+1];
				int b = st - 1;
				for (int j = st + 3; j <= en; j+=2) {
					if (P[j] >= P[st+1]) {
						if (P[j] < a) return false;
						a = P[j];
					} else {
						if (P[j] < b) return false;
						b = P[j];
					}
				}
			}
			st = en;
		}
	}

	return true;
}

