        if(i % 2 != p[i] % 2) f = false;
        invp[p[i]] = i;
    }
    SegmentTree ST(N);
    REP(i, N){
        int m = ST.get(i, N);
        if(m < INF && m % 2 == i % 2) f = false;
        ST.update(i, i);
    }


    Ans(f, "Yes", "No");

    return 0;
}

