int n;
set<pair<int,int> >odd,even;
int pos[300005];
int main(){
	scanf("%d",&n);
	for(int i=1;i<=n;i++){
		int x; scanf("%d",&x);
		if(i%2 == 1) odd.insert(pair<int,int>(i,x));
		else even.insert(pair<int,int>(i,x));
		pos[x] = i;
	}
	int curmin = 1;
	while(odd.size() || even.size()){
		assert(odd.size());
		pair<int,int>target = make_pair(pos[curmin],curmin);
		if(odd.find(target) == odd.end()){
			puts("No") ; return 0;
		}
		if(*odd.begin() == target){
			odd.erase(odd.begin());
			swap(odd,even);
			curmin++;
		}
		else{
			assert(even.size());
			{
				int i = 0;
				for(auto it=odd.begin(),it2 = even.begin();;it++,it2++,i++){
					if((*it) == target || i == 500){
						break;
					}
					if((*it).second < (*it2).second){
						puts("No"); return 0;
					}
				}
			}
			odd.erase(odd.find(target));
			pair<int,int>target2 = make_pair(pos[curmin+1],curmin+1);
			if(*even.begin() != target2){
				puts("No"); return 0;
			}
			even.erase(even.begin());
			curmin+=2;
		}
	}
	puts("Yes");
}

