class BIT{
    vl bit;
public:
    void BIT_init(ll n){
        bit.assign(n+1,0);
    }
    ll get_sum(int i){
        ll res=0;
        while(i>0){
            res+=bit[i];
            i-=i&(-i);
        }
        return res;
    }
    void add(int i,ll x){
        while(i<=bit.size()-1){
            bit[i]+=x;
            i+=i&(-i);
        }
    }
};

int main() {
    ios::sync_with_stdio(false);
    cin.tie(nullptr);
    int N;
    cin >> N;
    BIT bt;
    bt.BIT_init(N);
    vl p(N);
    rep(i,N){
        cin >> p[i];
        p[i]--;
