const int maxn = 300005;
const int inf = 1e9;

struct tr2 {
    int tr[maxn * 4];
    tr2() {
        fill(tr, tr + maxn, inf);
    }

    void set(int v, int l, int r, int i, int x) {
        if (l == r) {
            tr[v] = x;
            return;
        }
        int c = (l + r) >> 1;
        if (i <= c) {
            set(2 * v, l, c, i, x);
        } else {
            set(2 * v + 1, c + 1, r, i, x);
        }

        tr[v] = min(tr[2 * v], tr[2 * v + 1]);
    }

    int get(int v, int l, int r, int i, int j) {
        if (i <= l && r <= j) {
            return tr[v];
        }
        int res = inf;
        int c = (l + r) >> 1;
        if (i <= c) {
            chmin(res, get(2 * v, l, c, i, j));
        }
        if (c < j) {
            chmin(res, get(2 * v + 1, c + 1, r, i, j));
        }

        return res;
    }
};

