}
void solve(vector<int> &v, int st){
    if (st == v.size()) return;
    set<int> ms;
    for (int i=st; i < v.size(); i++){
        ms.insert(v[i]-st);
        set<int>::iterator it = ms.end();
        it--;
        int mx = *(it);
        if (mx == ms.size()){
            check(v, st, i);
            solve(v, i+1);
            return;
        }
    }
}
