    int N;
    cin >> N;

    vi p(N);
    vi move(N);

    for (int i = 0; i < N; i++) {
        cin >> p[i];
        --p[i];
        move[i] = p[i] == i ? 0 : p[i] > i ? 1 : -1;
    }

    auto proc = [&](vi& grp) {
        vi vals;

        int left = -1, right = -1;
        for (int i : grp) {
            int& which = move[i] == -1 ? left : right;
            if (which > p[i]) fail();
            which = p[i];

            vals.push_back(p[i]);
        }

        sort(all(vals));
        if (vals != grp) fail();
        grp.clear();
    };

    vi grp;
    for (int i = 0; i < N; i++) {
        if (i > 0 && !!move[i] == !!move[i-1]) {
            proc(grp);
        }
        if (move[i]) grp.push_back(i);
    }
    proc(grp);

    cout << "Yes\n";

    return 0;
}

