void msort(int s, int e){
	if(s + 1 == e) return;
	int m = s + (e - s) / 2, p = s, q = m, r = s;
	msort(s, m);
	msort(m, e);
	while(p < m || q < e){
		if(q == e || p < m && A[p] <= A[q]){
			T[r++] = A[p++];
		}
		else{
			T[r++] = A[q++];
			jon = (jon + m - p) % 3;
		}
	}
	for(r = s; r < e; ++r) A[r] = T[r];
}

