bool valid(vector<int> &seg, vector<int> &cor) {
	int n = (int) seg.size();
	auto ss = seg;
	auto sc = cor;
	sort(ss.begin(), ss.end());
	sort(sc.begin(), sc.end());
	if (ss != sc) {
		return false;
	}
	map<int, int> pos;
	rep(i, n) {
		pos[cor[i]] = i;
	}
	vector<int> l, r;
	rep(i, n) {
		if (i < pos[seg[i]]) {
			l.push_back(seg[i]);
		} else if (i > pos[seg[i]]) {
			r.push_back(seg[i]);
		} else {
			assert(false);
		}
	}
	rep(i, (int) l.size() - 1) {
		if (l[i] > l[i + 1]) {
			return false;
		}
	}
	rep(i, (int) r.size() - 1) {
		if (r[i] > r[i + 1]) {
			return false;
		}
	}
	return true;
}

