using namespace std;
const int N = 333333;
int n,a[N],T[N<<2];
void modify(int x,int y,int k=1,int l=1,int r=n){
    if(l==r){
        T[k]=y;
        return;
    }
    int h=l+r>>1;
    if(x<=h)
        modify(x,y,k<<1,l,h);
    else
        modify(x,y,k<<1|1,h+1,r);
    T[k]=max(T[k<<1],T[k<<1|1]);
}
int query(int L,int R,int k=1,int l=1,int r=n){
    if(L>R)
        return 0;
    if(L<=l&&r<=R)
        return T[k];
    int h=l+r>>1,e=0;
    if(L<=h)
        e=max(e,query(L,R,k<<1,l,h));
    if(h<R)
        e=max(e,query(L,R,k<<1|1,h+1,r));
    return e;
}
int chk(int x){
    if(x<=1||x>=n)
        return 0;
    if(a[x-1]<a[x]||a[x]<a[x+1])
        return 0;
    return max(query(1,x-2),a[x+1])==x-1;
}
queue<int> q;
int main(){
    int i;
    scanf("%d",&n);
    for(i=1;i<=n;i=i+1)
        scanf("%d",a+i),modify(i,a[i]);
    for(i=1;i<=n;i=i+1)
        if(chk(i))
            q.push(i);
    while(!q.empty()){
        i=q.front();
        //cout<<i;
        q.pop();
        modify(i-1,a[i+1]);
        modify(i+1,a[i-1]);
        swap(a[i-1],a[i+1]);
        if(chk(i-2))
            q.push(i-2);
        if(chk(i+2))
            q.push(i+2);
    }
    for(i=1;i<=n;i=i+1)
        if(a[i]!=i)
            break;
    if(i>n)
        cout<<"Yes";
    else
        cout<<"No";
}

