int b[maxn];
inline void solve(int l,int r) {
	for(int i = l;i <= r;i += 2) b[i] = 0;
	for(int i = l;i <= r;i += 2) b[p[i]] = i;
	int i = l,j = r;
	for(int min = 0;i <= r && b[i] && min <= b[i];i += 2) min = b[i];
	for(int min = 0;l <= j && b[j] && min <= b[j];j -= 2) min = b[i];
	if(j < i) for(int i = l;i <= r;++i) p[i] = i;
}
