		for(j = i + 1;j + 1 <= n && p[j] == j;j += 2);
		if(i <= j - 2) solve(i, j - 1), i = j - 1;
	}
	std::cout << (std::is_sorted(p + 1,p + n + 1) ? "Yes" : "No") << '\n';
}

