    require((r - l) % 2 == 0);
    assert(std::all_of(a.begin() + l, a.begin() + r + 1, [l, r](auto x){ return l <= x && x <= r; }));
    for (auto i = l + 1; i < r; i += 2)
      { require(a.at(i) == i); }
    auto L = std::find(a.begin() + l, a.begin() + r + 1, r) - a.begin();
    auto R = std::find(a.begin() + l, a.begin() + r + 1, l) - a.begin();
    auto c = std::vector< int >{};
    for (auto i = l; i <= r; i += 2)
      { c.emplace_back(a.at(i)); }
    std::adjacent_difference(all(c), c.begin());
    require(std::count_if(all(c), [](auto x){ return x < 0; }) <= 1);
  };
  auto l = -1;
  rep(i, 0, n + 1) {
    auto crr = a.at(i);

    if (set.empty()) {
      require(i <= crr);
      if (i < crr) {
        l = i;
        set.insert(crr);
      }
    }

    else {
      assert(0 <= l);
      require(l <= crr);
      if (i < crr) {
        set.insert(crr);
      }
      assert(i <= *set.begin());
      if (i == *set.begin()) {
        set.erase(set.begin());
        if (set.empty()) {
          cal(l, i);
        }
      }
    }
  }
  std::cout << "Yes" << std::endl;
  return 0;
}

