const int N = 330000;

int a[N], sum[N], prv[N], nxt[N];

int main() {

	ios::sync_with_stdio(0);
	cin.tie(0), cout.tie(0);

	int n; cin >> n;
	for (int i = 1; i <= n; i++) cin >> a[i], sum[i] = sum[i - 1] + (a[i] == i);
	for (int i = 1; i <= n; i++) {
		if (a[i] == i) {
			if (i - 2 >= 1 && a[i - 1] != i - 1 && a[i - 2] == i - 2) prv[i] = prv[i - 2];
			else prv[i] = i;
		}
	}
	for (int i = n; i >= 1; i--) {
		if (a[i] == i) {
			if (i + 2 <= n && a[i + 1] != i + 1 && a[i + 2] == i + 2) nxt[i] = nxt[i + 2];
			else nxt[i] = i;
		}
	}

	auto good = [&]() {
		for (int i = 1; i <= n; i++) if (a[i] != i) {
			int l = min(a[i], i), r = max(a[i], i);
			if ((r - l) & 1) return false;
			if (sum[r] - sum[l - 1] != (r - l) / 2) return false;
			if (a[l + 1] != l + 1) return false;
			if (a[r - 1] != r - 1) return false;
			if (nxt[l + 1] < r - 1) return false;
		}
		vector<pair<int, int>> V;
		for (int i = 1; i <= n; i++) if (a[i] > i) V.emplace_back(min(a[i], i), max(a[i], i));
		int Max = 0;
		for (auto it : V) {
			if (it.second < Max) return false;
			Max = max(Max, it.second);
		}
		V.clear();
		for (int i = 1; i <= n; i++) if (a[i] < i) V.emplace_back(min(a[i], i), max(a[i], i));
sort(V.begin(), V.end());
		Max = 0;
		for (auto it : V) {
			if (it.second < Max) return false;
			Max = max(Max, it.second);
		}
		return true;
	};

	cout << (good() ? "Yes" : "No") << "\n";

	return 0;
}

