int arr[300001];
int location[300001];
int tmp;

void swap(int ia, int ib) {
    tmp = arr[ia];
    arr[ia] = arr[ib];
    arr[ib] = tmp;
    location[arr[ia]] = ia;
    location[arr[ib]] = ib;
}

int main(int argc, const char * argv[]) {
    int n;
    int res = 1;
    cin >> n;

    for(int i = 1; i <= n; i++) {
        cin >> arr[i];
        location[arr[i]] = i;
    }

    for(int i = 1; i <= n; i++) {
        if((location[i] ^ i) & 1) {
            res = 0;
            goto out;
        }
    }

    for(int i = 1; i <= n; i++) {
        while(location[i] != i) {
            if(arr[location[i]-1] < i || arr[location[i]-2] < arr[location[i]-1]) {
                res = 0;
                goto out;
            }
            swap(location[i] - 2, location[i]);
        }
    }

out:

    if(res) {
        cout << "Yes\n";
    } else {
        cout << "No\n";
    }
}

