const int MAX_N = 3e5 + 10;

void No() { printf("No\n"); exit(0); }
void Yes() { printf("Yes\n"); exit(0); }

int N, P[MAX_N];
bool diff[MAX_N];

int main() {
    scanf("%d", &N);
    for (int i = 1; i <= N; i++) scanf("%d", &P[i]);
    for (int i = 1; i <= N; i++) diff[i] = P[i] != i;
    int curr = N;
    while (curr) {
        if (!diff[curr]) {
            curr--;
            continue;
        }
        int fir = curr;
        while (fir - 2 > 0 && diff[fir - 2]) fir -= 2;
        for (int i = curr; i >= fir; i -= 2) {
            if (P[i] > curr || P[i] < fir)
                No();
        }
        for (int i = curr - 1; i >= fir; i -= 2)
            if (diff[i])
                No();
        int t = curr;
        while (t >= fir && P[t] < t) t -= 2;
        while (t >= fir && P[t] > t) t -= 2;
        if (t >= fir)
            No();
        curr = fir - 1;
    }
    Yes();
}

