  std::set< int > set;
  auto cal = [&] (auto l, auto r) {
    require((r - l) % 2 == 0);
    for (auto i = l + 1; i < r; i += 2)
      { require(a.at(i) == i); }
    auto L = std::find(a.begin() + l, a.begin() + r + 1, r) - a.begin();
    auto R = std::find(a.begin() + l, a.begin() + r + 1, l) - a.begin();
    require(R - L == 2);
    require((L - l) % 2 == 0);
    for (auto i = l; i < L; i += 2)
      { require(a.at(i) == i + 2); }
    for (auto i = R + 2; i < r; i += 2)
      { require(a.at(i) == i - 2); }
  };
  auto l = -1;
  rep(i, 0, n + 1) {
    auto crr = a.at(i);

    if (set.empty()) {
      require(i <= crr);
      if (i < crr) {
        l = i;
        set.insert(crr);
      }
    }

    else {
      assert(0 <= l);
      require(l <= crr);
      if (i < crr) {
        set.insert(crr);
      }
      assert(i <= *set.begin());
      if (i == *set.begin()) {
        set.erase(set.begin());
        if (set.empty()) {
          cal(l, i);
        }
      }
    }
  }
