int n, a[1010100];

bool Solve(int x) {
  if(x > n) return 1;
  if(a[x] == x) return Solve(x + 1);
  int y = x;
  for(; y <= n; y += 2) {
    if(a[y + 1] != y + 1) break;
    if(a[y + 2] == y + 2) break;
  }

  int mi = 1e9, mx = 0;
  for(int i = x; i <= y; i++)
    mi = min(mi, a[i]), mx = max(mx, a[i]);
  if(mi != x || mx != y) return 0;

  vector<int> le, ri;
  for(int i = x; i <= y; i += 2) {
    if(a[i] > i) le.push_back(a[i]);
    else ri.push_back(a[i]);
  }
  for(int i = 1; i < le.size(); i++)
    if(le[i] < le[i - 1]) return 0;
  for(int i = 1; i < ri.size(); i++)
    if(ri[i] < ri[i - 1]) return 0;
  return Solve(y + 1);
}

