int merge(int *a,int l,int r){
	if(l==r)return 0;
	int mid=(l+r)>>1;
	int res=merge(a,l,mid)+merge(a,mid+1,r);
	int pl=l,pr=mid+1;
	for(int i=l;i<=r;i++){
		if(pl<=mid&&(a[pl]<a[pr]||pr>r))tmp[i]=a[pl++],res+=pr-mid-1;
		else tmp[i]=a[pr++];
	}
	for(int i=l;i<=r;i++)a[i]=tmp[i];
	return res;
}
