using namespace std;
const int N=300005;
int n,a[N],i,j,x,y,su[N],lb[N],id[N];
inline void O(){cout<<"No\n";exit(0);}
int bi[N];
inline void add(int x,int v){for(;x<=n;x+=x&-x)bi[x]+=v;}
inline int ask(int x){int ans=0;for(;x;x-=x&-x)ans+=bi[x];return ans;}
inline void solve(int l,int r){
	if(l==r){if(a[i]!=i)O();return;}
	int i,mi=1<<30,ma=0;
	for(i=r;i>=l;i-=2){
		mi=min(mi,a[i]);ma=max(ma,a[i]);
		y=ask(a[i]);
		x=y+(i-l)/2;
		if(y && (a[i]-l)/2<x)O();
		add(a[i],1);
	}
	for(i=l;i<=r;i+=2)add(a[i],-1);
	if(mi!=l || ma!=r)O();
}
int main(){
	ios::sync_with_stdio(0);cin.tie(0);
	cin>>n;
	for(i=1;i<=n;++i){
		cin>>a[i];id[a[i]]=i;
		x=a[i];y=i;if(x>y)swap(x,y);
		if((y-x)&1)O();++su[x+1];--su[y+1];
	}
	for(i=1;i<=n;++i)lb[i]=n+1;
	for(i=2;i<=n;++i)su[i]+=su[i-2];
	for(i=1;i<=n;++i)if(su[i] && a[i]!=i)O();
	for(i=1;i<=n;++i)if(su[i] && su[i+1])O();
	for(i=1;i<=n;++i){
		x=i;y=a[i];if(x>y)swap(x,y);
		lb[y]=min(lb[y],x);
	}
	for(i=n;i;--i)lb[i-1]=min(lb[i-1],lb[i]);
	for(i=1;i<=n;i=j){
		for(j=i;lb[j]==lb[i];++j);
		solve(i,j-1);
	}
//	for(i=1;i<=n;++i)if(lb[i]!=lb[i+1])solve(lb[i],i);
	cout<<"Yes\n";
	return 0;
}

