class Fenwick{
public:
	int t[maxn];
	void add(int v,int val){
		for(;v<=n;v+=(v&-v))t[v]+=val;
	}
	int sum(int v){
		int res=0;
		for(;v;v-=(v&-v))res+=t[v];
		return res;
	}
}Tree[2];

int main(){
	scanf("%d",&n);
	bool ok=true;
	int last=-2,lastval=-1,plast[2]={-1,-1};
	REP(i,n){
		scanf("%d",&p[i]);
		ok&=((i&1)==(p[i]&1));
		int ori=p[i];
		ori+=2*((i-1)/2-Tree[i&1].sum(p[i]));
		if(i<ori){
			ok&=(i>plast[(i+1)&1]);
			ok&=(i>last+1);
			ok&=(p[i]>lastval);
			last=i;
			lastval=p[i];
			plast[i&1]=ori;
		}
		Tree[i&1].add(p[i],1);
	}
	puts(ok?"Yes":"No");
	return 0;
}

