int n;
int p[300010];
ll t[600010];
ll t2[600010];
void upd(int p, int val) {
    for (t[p += n] = val; p > 1; p >>= 1) t[p >> 1] = t[p] + t[p ^ 1];
}
ll qry(int l, int r) {
    ll ret = 0;
    for (l += n, r += n; l < r; l >>= 1, r >>= 1) {
        if (l & 1) ret += t[l++];
        if (r & 1) ret += t[--r];
    }
    return ret;
}
void init() {
    rep(i, n) t2[i + n] = p[i];
    for (int i = n - 1; i > 0; --i) t2[i] = t2[i << 1] + t2[i << 1 | 1];
}
ll qry2(int l, int r) {
    ll retl = 0, retr = 0;
    for (l += n, r += n; l < r; l >>= 1, r >>= 1) {
        if (l & 1) retl = max(retl, t[l++]);
        if (r & 1) retr = max(retr, t[--r]);
    }
    return max(retl, retr);
}
bool ispos(int l, int r) {
    ll cnt = 0;
    memset(t, 0, sizeof(n));
    repp(i, l, r) {
        cnt += qry(p[i], n);
        upd(p[i] - 1, 1);
    }
    if (cnt % 3) return 0;
    return 1;
}

