    }
    init();
    int prv = 0;
    int ans = 1;
    rep(i, n) {
        if (qry2(prv, i + 1) == i + 1) {
            ans &= ispos(prv, i + 1);
            prv = i + 1;
        }
    }
    cout << (ans ? "Yes\n" : "No\n");
}

#include<iostream> 
using namespace std;

const int maxn = 3e5 + 50;
int N, p[maxn];

bool check(int l, int r) {
	for (int i = l; i <= r; ++i) {
		if (p[i]<l || p[i]>r) {
			return false;
		}
	}
	int L = -1, R = -1;
	for (int i = l; i <= r; i += 2) {
		if (p[i] < i) {
			if (L > p[i]) return false;
			L = p[i];
		}
		else {
			if (R > p[i]) return false;
			R = p[i];
		}
	}
	return true;
}

bool sol() {
	for (int i = 2; i < N; ++i) {
		if (p[i - 1] != i - 1 && p[i] != i && p[i + 1] != i + 1) {
			return false;
		}
	}
	int l = 1;
	while (l <= N) {
		while (l <= N && p[l] == l) ++l;
		if (l > N) break;
		int r = l;
		while (r + 2 <= N && p[r + 1] == r + 1 && p[r + 2] != r + 2) r += 2;
		if (!check(l, r)) return false;
		l = r + 1;
	}
	return true;
}

int main() {
	scanf("%d", &N);
	for (int i = 1; i <= N; ++i) {
		scanf("%d", &p[i]);
	}
	puts(sol() ? "Yes" : "No");

	return 0;
}

