bool solve(int n, vector<int> p) {
    REP (i, n) {
        if (p[i] % 2 != i % 2) {
            return false;
        }
    }

    int l[2] = {};
    int m[2] = {};
    REP (i, n) {
        bool q = i % 2;
        int l1 = 0, r1 = 0;
        for (int j = not q; j < n; j += 2) {
            if (j < i) {
                l1 += (p[j] > p[i]);
            } else if (i < j) {
                r1 += (p[i] > p[j]);
            }
        }
        int m1 = min(l1, r1);
        l1 -= m1;
        r1 -= m1;
// cerr << i << " " << p[i] << " : " << l1  <<" / " << m1 <<" / " << r1<< endl;

        l[    q] += l1;
        l[not q] -= m1;
        m[    q] += m1;
        m[not q] -= r1;
        // if (l[not q] < 0) return false;
        // if (m[not q] < 0) return false;
    }
    if (l[false]) return false;
    if (l[ true]) return false;
    if (m[false]) return false;
    if (m[ true]) return false;
    return true;
}

int main() {
    int n; cin >> n;
    vector<int> p(n);
    REP (i, n) {
        cin >> p[i];
        -- p[i];
    }
    cout << (solve(n, p) ? "Yes" : "No") << endl;
    return 0;
}

