bool solve() {
    vector<int> maxLeftP(N+1, 0), minRightP(N+1, N-1);
    for (int i = 0; i < N; ++i) maxLeftP[i+1] = max(maxLeftP[i], P[i]);
    for (int i = N; i >= 1; --i) minRightP[i-1] = min(minRightP[i], P[i-1]);
    for (int i = 0; i < N-1; ++i) {
        if (P[i] == i && P[i+1] == i+1) { if (maxLeftP[i+1] != i) return false; }
