    }
    for (int i = 1; i+1 < N; ++i) {
        if (P[i] > i) { if (maxLeftP[i] > P[i]) return false; }
        else if (P[i] < i) { if (minRightP[i] < P[i]) return false; }
    }
    return true;
}

