const int N = 3e5 + 5;

#define die() exit(0 * puts("No"));

int A[N], n;

void solve(int x) {
  if(x == n + 1) return;
  if(A[x] == x) {
    solve(x + 1);
    return;
  }
  int y;
  for(y = x; y <= n; y += 2) {
    if(A[y + 1] != y + 1 || A[y + 2] == y + 2) break;
  }
  if(y == n + 1) y -= 2;
  if(x == y) die();
  vector <int> L, R;
  L.clear(), R.clear();
  for(int i = x; i <= y; i += 2) {
    if(A[i] > i) L.push_back(A[i]);
    else R.push_back(A[i]);
    if(A[i] < x || A[i] > y) die();
  }
  for(int i = 0; i < (int) L.size() - 1; ++ i) if(L[i] > L[i + 1]) die();
  for(int i = 0; i < (int) R.size() - 1; ++ i) if(R[i] > R[i + 1]) die();
  solve(y + 1);
}

main(void) {
  cin >> n;
  for(int i = 1; i <= n; ++ i) scanf("%d", &A[i]);
  solve(1);
  puts("Yes");
  return 0;
