int n, perm[N], bit[N];
long long inv1 = 0ll, inv2 = 0ll, inv = 0ll;
int lowbit (int x) {
	return x & -x;
}
void add (int x) {
	for (; x <= n; x += lowbit(x)) bit[x]++;
}
int query (int x) {
	int ans = 0;
	for (; x; x ^= lowbit(x)) ans += bit[x];
	return ans;
}

