int a[maxn];
bool solve(int L,int R){
	if((R-L)&1) return 1;
	if(L==R) return 0;
	for(int i=L+1;i<R;i+=2) if(i!=a[i]) return 1;
	int x=0,y=maxn;
	for(int i=L;i<=R;i+=2){
		if(a[i]<i){
			if(x>=a[i]) return 1;
			x=a[i];
		}
		else if(a[i]>i){
			if(y<=a[i]) return 1;
			y=a[i];
		}
		else return 1;
	}
	return 0;
}
